"use client"

import { motion, AnimatePresence } from "framer-motion";

interface WaveInTextProps {
  children: string;
  className?: string;
  duration?: number; // animation duration per character
  yOffset?: number; // vertical offset for entrance
  blurAmount?: number; // initial blur
  isVisible?: boolean; // control exit
}

export const WaveInText = ({
  children,
  className,
  duration = 0.5,
  yOffset = 20,
  blurAmount = 8,
  isVisible = true,
}: WaveInTextProps) => {
  // Split text into characters including spaces
  const characters = children.split("").map((char) =>
    char === " " ? "\u00A0" : char
  );

  return (
    <AnimatePresence>
      {isVisible && (
        <span className={`inline-block text-[17px] ${className}`}>
          {characters.map((char, index) => (
            <motion.span
              key={index}
              style={{ display: "inline-block" }}
              initial={{ opacity: 0, y: yOffset, filter: `blur(${blurAmount}px)` }}
              animate={{ opacity: 1, y: 0, filter: "blur(0px)" }}
              exit={{ opacity: 0, y: yOffset, filter: `blur(${blurAmount}px)` }}
              transition={{
                duration,
                delay: index * 0.05,
                ease: "easeOut",
              }}
            >
              {char}
            </motion.span>
          ))}
        </span>
      )}
    </AnimatePresence>
  );
};

import { WaveInText } from "@/components/wave-in-text";


export function WaveInTextDemo (){

  return (
      <WaveInText>
         Devouring Details Devouring Details Devouring Details
      </WaveInText>

    
  )
}
